for ($i=0; $i <count($product) ; $i++) {
	$existe = "SELECT count(*) from product where sku ='".$product[$i]['sku']."'";
	$stm = $pst->query($existe,PDO::FETCH_NUM);
	$res = $stm->fetch();

	if($res[0]>0) {
		echo "existe : ".$res[0].' ';

		$data = [
			'name' =>$product[$i]['name'], 'price' => $product[$i]['price'], 'pricebuy' => $product[$i]['buy'], 'quantity' => $product[$i]['canty'],
			'iva' => '0.12', 'state' => '1', 'id_marca' => '1', 'id_size' => '1', 'id_category' => '1', 'id_office' => '3', 'sku' =>$product[$i]['sku']
		];

