		];

		$sqlinsert = "INSERT INTO product (sku, name, price, pricebuy, quantity, iva, state, id_marca, id_size, id_category, id_office) VALUES (:sku, :name, :price, :pricebuy, :quantity, :iva, :state, :id_marca, :id_size, :id_category, :id_office);";
		$statementu = $pst->prepare($sqlinsert);		
		$statementu->execute($datau);
	}
